import { createMarkdown } from '@vuepress/markdown';
import { highlightPlugin, preWrapperPlugin, resolveHighlighter } from '@vuepress/plugin-prismjs';
import { lineNumbers as lineNumbersPlugin } from '@vuepress/highlighter-helper';
const md = createMarkdown();
md.options.highlight = (code, lang) => {
    const highlighter = resolveHighlighter(lang);
    return highlighter?.(code) || '';
};
md.use(highlightPlugin);
md.use(preWrapperPlugin, { preWrapper: true });
md.use(lineNumbersPlugin, { lineNumbers: true, removeLastLine: true });
export { md };
